import React, {useState, useEffect} from "react"

const Context = React.createContext()

function ContextProvider({children}) {

    const [records, setRecords] = useState([])
    const [searcherValue, setSearcherValue] = useState("")


    const baseUrl = "https://api.airtable.com/v0/appduyTcvGxpEgTb8/tblMpliaVkCLsiKnC?view=%D0%92+%D0%BD%D0%B0%D0%BB%D0%B8%D1%87%D0%B8%D0%B8"

      useEffect(() => {
          fetch(baseUrl, {
              headers: {
                  'Authorization': 'Bearer keymV3iplyx82ryEd'
              }
          })
              .then(res => res.json())
              .then(data => setRecords(data.records))
      }, [])

    const handleSearcher = (event) => {
        setSearcherValue(prevValue => (
            prevValue = event.target.value
        ))
    }

    useEffect(() => {
        if (searcherValue.length > 0) {
            setRecords(prevRecords => (
                prevRecords.filter(record =>(
                    record.fields.Цена == 399
                ))
            ))
        }
    }, [searcherValue])

    console.log(records)

    return (
        <Context.Provider value={{
            records,
            searcherValue,
            setSearcherValue,
            handleSearcher
        }}>
            {children}
        </Context.Provider>
    )
}

export {ContextProvider, Context}
