import {useParams, Link} from "react-router-dom"
import { useState, useContext } from "react"
import { Context } from "../../Context"
import ProductCard from "../elements/ProductCard";

export default function Cart() {

    const {cartItems, records} = useContext(Context)

    /*
    const productsInCart = cartItems.map(item => (
        <ProductCard
            categoryName={"all"}
            record={records.filter(r => r.id === Object.keys(item)[0])}
            key={Object.keys(item)[0]}
            view={"small"}
        />
    ))
    */

    console.log(cartItems)

    const productsInCart = Object.fromEntries (
        Object.entries(cartItems).map(([key, value]) => (
            <ProductCard
                categoryName={"all"}
                record={records.filter(r => r.id === key)[0]}
                key={key}
                view={"small"}
            />
        )) 
    )

    return (
        <div className="cart">
            <div className="fix-wrapper">
                <h1 className='catalog--title'>Корзина</h1>
            </div>
            { productsInCart }
        </div>
    )
}
