import { useState, useContext } from "react"
import { Context } from "../../Context"

export default function ContactsWindow() {

    const [userPhone, setUserPhone] = useState("+7")
    const [useName, setUserName] = useState("")

    const applyMask = (number) => (
        `${number.substring(0, 2)} ${number.substring(2, 3)} ${number.substring(3, number.length)}`.trim()
    )

    const handlePhone = (event) => {
        setUserPhone(prevValue => {
            applyMask(event.target.value)  
        })
    }

    const handleName = (event) => {
        setUserName(prevValue => (event.target.value))
    }



    return (
        <div className="contacts-window">
            <p>Номер телефона для связи</p>
                <input
                    className="userPhone"
                    type="text"
                    name="userPhone"
                    value={userPhone}
                    onChange={handlePhone}
                    placeholder="+7"
                    autoFocus={true}
                />
            <p>Имя</p>
                <input
                    className="useName"
                    type="text"
                    name="useName"
                    value={useName}
                    onChange={handleName}
                    autoFocus={false}
                />
        </div>
    )
}
