import { useContext, useEffect } from "react"
import { Context } from "../../Context"

export default function Counter({ recordId }) {

    const {cartItems, setCartItems} = useContext(Context)

    console.log(cartItems)

    const oneMoreItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] + 1
            }
        ))
    }

    const oneLessItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] > 1? prev[recordId] - 1 : 0
            }
        ))
        setCartItems(prev => (
            Object.fromEntries (
                Object.entries(prev).filter(([key, value]) => value > 0) 
            )
        ))
    }

    return (
        <div className="counter">
            <div onClick={oneLessItem}>
                <svg className="counter--minus" width="7" height="2" viewBox="0 0 7 2">
                    <line id="line_1" x2="5" transform="translate(-2.5 1)" fill="white" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
                </svg>
            </div>
            <span className="counter--number">{cartItems[recordId]}</span>
            <div onClick={oneMoreItem}>
            <svg id="Компонент_1_1" data-name="Компонент 1 – 1" width="20" height="20" viewBox="0 0 20 20">
                <rect id="Прямоугольник_43" data-name="Прямоугольник 43" width="20" height="20" fill="rgba(255,255,255,0)"/>
                <path id="Контур_6" data-name="Контур 6" d="M421,519.609v16.362" transform="translate(-411 -517.79)" fill="none" stroke="#fff" stroke-linecap="round" stroke-width="1"/>
                <path id="Контур_7" data-name="Контур 7" d="M421,519.609v16.362" transform="translate(-517.79 431) rotate(-90)" fill="none" stroke="#fff" stroke-linecap="round" stroke-width="1"/>
            </svg>
            </div>
        </div>
    )
}
