import { useState, useContext } from "react"
import { Context } from "../../Context"

export default function ContactsWindow({requestOrder}) {

    const [userPhone, setUserPhone] = useState("+7")
    const [userName, setUserName] = useState("")
    const {leftEmptyPhone, setLeftEmptyPhone} = useContext(Context)
    const {isContactsWindowShown, setIsContactsWindowShown} = useContext(Context)

    const handlePhone = (event) =>
        setUserPhone(prevValue => (event.target.value))

    const handleName = (event) =>
        setUserName(prevValue => (event.target.value))

    return (
        <div className={`contacts-window ${isContactsWindowShown}`}>
            <p className={`${leftEmptyPhone}`}>Номер телефона для связи</p>
                <input
                    className="userPhone"
                    type="text"
                    name="userPhone"
                    value={userPhone}
                    onChange={handlePhone}
                    placeholder="+7"
                    autoFocus={true}
                />
            <p>Имя</p>
                <input
                    className="useName"
                    type="text"
                    name="useName"
                    value={userName}
                    onChange={handleName}
                    autoFocus={false}
                />
            <div className="wrapper">
                <button
                    className="send"
                    onClick={
                        () => userPhone.length >= 10? setIsContactsWindowShown("hidden") : setLeftEmptyPhone("empty")
                    }
                >
                    Сделать заказ
                </button>
                <button
                    className="cancel"
                    onClick={() => setIsContactsWindowShown("hidden")}
                >
                    Назад
                </button>
            </div>
        </div>
    )
}
