import { useState, useContext } from "react"
import { Context } from "../../Context"

export default function ContactsWindow() {

    const [userPhone, setUserPhone] = useState("+7")
    const [useName, setUserName] = useState("")

    const handleSearcher = (event) => {
        setUserPhone(prevValue => (
            [event.target.name] = event.target.value
        ))
        setUserName(prevValue => (
            [event.target.name] = event.target.value
        ))
    }

    return (
        <div className="contacts-window">
                <input
                    className="userPhone"
                    type="text"
                    name="userPhone"
                    value={userPhone}
                    onChange={handleSearcher}
                    placeholder="placeholder"
                    autoFocus={true}
                />
                <input
                    className="useName"
                    type="text"
                    name="useName"
                    value={useName}
                    onChange={handleSearcher}
                    placeholder="placeholder"
                    autoFocus={false}
                />
        </div>
    )
}
