import {useParams} from "react-router-dom"
import { useState, useContext } from "react"
import { Context } from "../../Context"
import ProductsList from "../elements/ProductsList";
import Searcher from '../elements/Searcher';

function Catalog(props) {


  const {filteredRecords} = useContext(Context)
  const params = useParams()

  console.log(params.filter)

  const writePlaceholder = () => {
    if (params.filter === "all") {
      return "Поиск по всем товарам"
    } else {
      return "Поиск в этой категории"
    }
  }

  return (
    <div className="catalog">
      <h1 className='catalog--title'>Каталог товаров</h1>
      <Searcher placeholder={writePlaceholder()}/>
      <ProductsList records={filteredRecords[`${params.filter}`]}/>
    </div>
  );
}

export default Catalog
