import { useContext, useEffect } from "react"
import { Context } from "../../Context"

export default function Counter({ recordId }) {

    const {cartItems, setCartItems} = useContext(Context)

    // useEffect(() => {
    //     setCartItems(
    //         Object.keys(cartItems).filter(key => (
    //             cartItems[key] > 0
    //         ))
    //     )
    // }, [])

    const oneMoreItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] + 1
            }
        ))
    }

    const oneLessItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId]>1? prev[recordId] - 1 : 0
            }
        ))
        setCartItems( prev => (
                prev[recordId] < 1 &&
                    Object.keys(cartItems).filter(key => (
                    cartItems[key] > 0
                ))
            )
        )
    }

    return (
        <div className="counter">
            <svg onClick={oneLessItem} className="counter--minus" xmlns="http://www.w3.org/2000/svg" width="7" height="2" viewBox="0 0 7 2">
                <line id="Линия_1" x2="5" transform="translate(-2.5 1)" fill="none" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
            </svg>
            <span className="counter--number">{cartItems[recordId]}</span>
            <svg onClick={oneMoreItem}  className="counter--plus" xmlns="http://www.w3.org/2000/svg" width="9" height="9" viewBox="0 0 9 9">
                <line id="Линия_3" x2="7" transform="translate(-.75 0)" fill="none" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
                <line id="Линия_4" y2="7" transform="translate(2.75 -3.25)" fill="none" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
            </svg>
        </div>
    )
}
