import { useContext, useRef } from "react"
import { Context } from "../../Context"
import PostGenerator from "./PostGenerator"
import html2canvas from "html2canvas"

export default function ContactsWindow({requestOrder}) {

    const {
        userPhone,
        setUserPhone,
        userName,
        setUserName,
        leftEmptyPhone,
        setLeftEmptyPhone,
        isContactsWindowShown,
        setIsContactsWindowShown
    } = useContext(Context)

    const exportRef = useRef()
    const handlePhone = (event) => setUserPhone((event.target.value))
    const handleName = (event) => setUserName((event.target.value))

    const generatePost = (el, name) => {
        exportAsImage(el, name)
    }

    const exportAsImage = (element, imageFileName) => {
        html2canvas(element).then(canvas => {
            const image = canvas.toDataURL("image/png", 1.0);
            downloadImage(image, imageFileName);
        })
    }

    const downloadImage = (blob, fileName) => {
        const fakeLink = window.document.createElement("a");
        fakeLink.style = "display:none;";
        fakeLink.download = fileName;
        fakeLink.href = blob;
        document.body.appendChild(fakeLink);
        fakeLink.click();
        document.body.removeChild(fakeLink);
        fakeLink.remove();
    };

    return (
        <div className={`contacts-window ${isContactsWindowShown}`}>
            <PostGenerator
                userPhone={userPhone}
                userName={userName}
                ref={exportRef}
            />
            <p className={`${leftEmptyPhone}`}>Номер телефона для связи</p>
                <input
                    className="userPhone"
                    type="text"
                    name="userPhone"
                    value={userPhone}
                    onChange={handlePhone}
                    placeholder="+7"
                    autoFocus={true}
                />
            <p>Имя</p>
                <input
                    className="useName"
                    type="text"
                    name="useName"
                    value={userName}
                    onChange={handleName}
                    autoFocus={false}
                />
            <div className="wrapper">
                <button
                    className="send"
                    onClick={
                        () =>
                        !userPhone.includes("://")?
                            userPhone.length >= 10?
                            requestOrder() :
                            setLeftEmptyPhone("empty")
                        : generatePost(exportRef.current, "test")
                    }
                >
                    Сделать заказ
                </button>
                <button
                    className="cancel"
                    onClick={() => setIsContactsWindowShown("hidden")}
                >
                    Назад
                </button>
            </div>
        </div>
    )
}
