import { Link, useParams } from 'react-router-dom'
import ProductCard from '../elements/ProductCard'
import { Context } from '../../Context'
import { useContext, useState, useEffect } from 'react'

export default function ProductInfo(props) {

    const {records, currentCategory, setRecords} = useContext(Context)
    const params = useParams()

    const baseUrl = "https://api.airtable.com/v0/appduyTcvGxpEgTb8/tblMpliaVkCLsiKnC?view=%D0%92+%D0%BD%D0%B0%D0%BB%D0%B8%D1%87%D0%B8%D0%B8"
    useEffect(() => {
        fetch(baseUrl, {
            headers: {
                'Authorization': 'Bearer keymV3iplyx82ryEd'
            }
        })
            .then(res => res.json())
            .then(data => (setRecords(data.records)))
    }, [])

    return (
        <div className="product-info">
            <div className="fix-wrapper">
                <div className='catalog--back'>
                <Link to={`/catalog/${currentCategory}`}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="15.127" height="23.983" viewBox="0 0 15.127 23.983">
                    <path id="back" d="M18.141.857,6,13,17.841,24.84l2.985-2.985L11.969,13l9.158-9.158Z" transform="translate(-6 -0.857)" fillRule="evenodd"/>
                    </svg>
                </Link>
                <Link to={`/catalog/${currentCategory}`}>
                    <h3>Назад</h3>
                </Link>
                </div>
            </div>
            <ProductCard
                record={records[records.findIndex(el => el.id === params.id)]}
            />
            <div>
                additional info
            </div>
        </div>
    )
}
