import { useContext, useEffect } from "react"
import { Context } from "../../Context"

export default function Counter({ recordId }) {

    const {cartItems, setCartItems} = useContext(Context)

    console.log(cartItems)

    const oneMoreItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] + 1
            }
        ))
    }

    const oneLessItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] > 1? prev[recordId] - 1 : 0
            }
        ))
        setCartItems(prev => (
            Object.fromEntries (
                Object.entries(prev).filter(([key, value]) => value > 0) 
            )
        ))
    }

    return (
        <div className="counter">
            <div onClick={oneLessItem}>
                <svg width="10" height="10" viewBox="0 0 10 10">
                    <rect width="10" height="10" fill="rgba(255,255,255,0)"/>
                    <path d="M0,0V6.362" transform="translate(1.819 5) rotate(-90)" fill="none" stroke="#fff" stroke-linecap="round" stroke-width="1"/>
                </svg>
            </div>
            <span className="counter--number">{cartItems[recordId]}</span>
            <div onClick={oneMoreItem}>
                <svg width="15" height="15" viewBox="0 0 15 15">
                    <rect width="10" height="10" fill="rgba(255,255,255,0)"/>
                    <path d="M421,519.609v6.362" transform="translate(-416 -517.79)" fill="none" stroke="#fff" stroke-linecap="round" stroke-width="1"/>
                    <path d="M0,0V6.362" transform="translate(1.819 5) rotate(-90)" fill="none" stroke="#fff" stroke-linecap="round" stroke-width="1"/>
                </svg>
            </div>
        </div>
    )
}
