import { useContext, useEffect } from "react"
import { Context } from "../../Context"

export default function Counter({ recordId }) {

    const {cartItems, setCartItems} = useContext(Context)

    console.log(cartItems)

    const oneMoreItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] + 1
            }
        ))
    }

    const oneLessItem = () => {
        setCartItems(prev => (
            {
                ...prev,
                [recordId]: prev[recordId] > 1? prev[recordId] - 1 : 0
            }
        ))
        setCartItems(prev => (
            Object.fromEntries (
                Object.entries(prev).filter(([key, value]) => value > 0) 
            )
        ))
    }

    return (
        <div className="counter">
            <div>
                <svg onClick={oneLessItem} className="counter--minus" width="7" height="2" viewBox="0 0 7 2">
                    <line id="line_1" x2="5" transform="translate(-2.5 1)" fill="white" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
                </svg>
            </div>
            <span className="counter--number">{cartItems[recordId]}</span>
            <div>
                <svg onClick={oneMoreItem}  className="counter--plus" width="9" height="9" viewBox="0 0 9 9">
                    <line id="line_3" x2="7" transform="translate(-.75 0)" fill="white" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
                    <line id="line_4" y2="7" transform="translate(2.75 -3.25)" fill="white" stroke="#fff" strokeLinecap="round" strokeWidth="2"/>
                </svg>
            </div>
        </div>
    )
}
