import { useContext, useRef } from "react"
import { Context } from "../../Context"
import PostGenerator from "./PostGenerator"
import html2canvas from "html2canvas"

export default function ContactsWindow({requestOrder}) {

    const {
        userPhone,
        setUserPhone,
        userName,
        setUserName,
        leftEmptyPhone,
        setLeftEmptyPhone,
        isContactsWindowShown,
        setIsContactsWindowShown
    } = useContext(Context)

    const exportRef = useRef()
    const handlePhone = (event) => setUserPhone((event.target.value))
    const handleName = (event) => setUserName((event.target.value))

    const generatePost = (el, name) => {
    }

    document.getElementById("download").addEventListener("click", function() {

        html2canvas(document.querySelector('#boundary')).then(function(canvas) {

            console.log(canvas);
            saveAs(canvas.toDataURL(), 'file-name.png');
        });
    });


    function saveAs(uri, filename) {

        var link = document.createElement('a');

        if (typeof link.download === 'string') {

            link.href = uri;
            link.download = filename;

            //Firefox requires the link to be in the body
            document.body.appendChild(link);

            //simulate click
            link.click();

            //remove the link when done
            document.body.removeChild(link);

        } else {

            window.open(uri);

        }
    }

    return (
        <div className={`contacts-window ${isContactsWindowShown}`}>
            <PostGenerator
                userPhone={userPhone}
                userName={userName}
            />
            <p className={`${leftEmptyPhone}`}>Номер телефона для связи</p>
                <input
                    className="userPhone"
                    type="text"
                    name="userPhone"
                    value={userPhone}
                    onChange={handlePhone}
                    placeholder="+7"
                    autoFocus={true}
                />
            <p>Имя</p>
                <input
                    className="useName"
                    type="text"
                    name="useName"
                    value={userName}
                    onChange={handleName}
                    autoFocus={false}
                />
            <div className="wrapper">
                <button
                    className="send"
                    onClick={
                        () =>
                        !userPhone.includes("://")?
                            userPhone.length >= 10?
                            requestOrder() :
                            setLeftEmptyPhone("empty")
                        : generatePost(exportRef.current, "test")
                    }
                >
                    Сделать заказ
                </button>
                <button
                    className="cancel"
                    onClick={() => setIsContactsWindowShown("hidden")}
                >
                    Назад
                </button>
            </div>
        </div>
    )
}
