import { Link, useParams } from 'react-router-dom'
import ProductCard from '../elements/ProductCard'
import { Context } from '../../Context'
import { useContext, useState, useEffect } from 'react'

export default function ProductInfo(props) {


    const [records, setRecords] = useState([])
    const baseUrl = "https://api.airtable.com/v0/appduyTcvGxpEgTb8/tblMpliaVkCLsiKnC?view=%D0%92+%D0%BD%D0%B0%D0%BB%D0%B8%D1%87%D0%B8%D0%B8"
    useEffect(() => {
        fetch(baseUrl, {
            headers: {
                'Authorization': 'Bearer keymV3iplyx82ryEd'
            }
        })
            .then(res => res.json())
            .then(data => (setRecords(data.records)))
    }, [])

    useEffect(() => {
        (async function() {
            try {
                const response = await fetch(baseUrl);
                const json = await response.json();
                setRecords(json.data.records);
            } catch (e) {
                console.error(e);
            }
        })();
    }, []);

    //const {records/*, currentCategory*/} = useContext(Context)
    const currentCategory = 'all'
    const params = useParams()
    const chosenRecord = records[records.findIndex(el => el.id === params.id)]

    return (
        <div className="product-info">
            <div className="fix-wrapper">
                <div className='catalog--back'>
                <Link to={`/catalog/${currentCategory}`}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="15.127" height="23.983" viewBox="0 0 15.127 23.983">
                    <path id="back" d="M18.141.857,6,13,17.841,24.84l2.985-2.985L11.969,13l9.158-9.158Z" transform="translate(-6 -0.857)" fillRule="evenodd"/>
                    </svg>
                </Link>
                <Link to={`/catalog/${currentCategory}`}>
                    <h3>Назад</h3>
                </Link>
                </div>
            </div>
            <ProductCard
                record={chosenRecord}
                view={"big"}
            />

            <div className="text-information">
                <article>
                    <h3>Описание</h3>
                    <p>{chosenRecord.fields.Описание}</p>
                </article>
                <article>
                    <h3>Способ применения</h3>
                    <p>{chosenRecord.fields['Способ применения']}</p>
                </article>
                <article>
                    <h3>Бренд</h3>
                    <p>{chosenRecord.fields.Бренд}</p>
                </article>
                <article>
                    <h3>Объём</h3>
                    <p>{chosenRecord.fields.Объём}</p>
                </article>
                <article>
                    <h3>Теги</h3>
                    <p>{chosenRecord.fields.Тэги.join(', ')}</p>
                </article>
            </div>
        </div>
    )
}
